  @tailrec
  def findFirstBelow1(input: String, currentIndex: Int = 0, currentLevel: Int = 0): Int = currentLevel match{
    case -1 => currentIndex
    case _ => findFirstBelow1(input, currentIndex + 1, currentLevel + (if(input(currentIndex) == '(') 1 else -1))
