  //original solution was in the REPL
  def main(args: Array[String]) {
    println("Part 1: " + calculateLevel(input))
    println("Part 2: " + findFirstBelow1(input))
  }

  private def toLevelScore: Char => Int = {
    case '(' => 1
    case _ => -1
  }

  def calculateLevel(input: String): Int =
    input.map(toLevelScore).sum

  def findFirstBelow1(input: String): Int = {
    @tailrec
    def go(currentIndex: Int, currentLevel: Int): Int =
      currentLevel match {
        case -1 => currentIndex
        case _ => go(currentIndex + 1, currentLevel + toLevelScore(input(currentIndex)))
      }

    go(0, 0)
  }
